import React, { useRef, useState } from "react";
import Layout from "../../Layout";
import { Row, Col, Button, Input, Image } from "antd";
import type { InputRef } from 'antd';
import './add.scss'


const Add = () => {

    const onChange = (e: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement>) => {
        console.log('Change:', e.target.value);
    };

    const { TextArea } = Input;
    const inputRef = useRef<InputRef | null>(null);
    const [image,setImage]=useState('');


    const handleImageClick = () => {
        if (inputRef.current?.input) {
            inputRef.current.input.click();
        }
    };

    return (
        <>
            <Layout>
                <Row>
                    <Col span={24} className="title">
                        <h1>ADD BOOK</h1>
                    </Col>
                    <Col span={4} offset={10} onClick={handleImageClick} className="imagePicker">
                        {inputRef ?
                            <Image
                                width='200px'
                                height='200px'
                                preview={false}
                                src="error"
                                fallback="data:image/png;base64,"
                            /> :
                            <Image
                                width={200}
                                src="https://zos.alipayobjects.com/rmsportal/jkjgkEfvpUPVyRjUImniVslZfWPnJuuZ.png"
                            />}
                        <Input type="file" ref={inputRef} style={{ display: 'none' }} />
                    </Col>
                    <Col span={6} offset={9} className="title__input">
                        <Input showCount maxLength={20} onChange={onChange} />
                    </Col>
                    <Col span={8} offset={8} className="content__input">
                        <TextArea
                            showCount
                            maxLength={100}
                            onChange={onChange}
                            placeholder="disable resize"
                            style={{ height: 120, resize: 'none' }}
                        />
                    </Col>
                    <Col span={8} offset={8} className="submitButton">
                        <Button type="primary">Submit</Button>
                    </Col>

                </Row>
            </Layout>
        </>
    );
}

export default Add;